import"./chunks/theme.BE6tHNFg.js";import{c as o,o as e,ag as r}from"./chunks/framework.Drb80Ych.js";const c=JSON.parse('{"title":"Каскадность стилей","description":"","frontmatter":{"outline":[2,3]},"headers":[],"relativePath":"css/selectors/page_007.md","filePath":"css/selectors/page_007.md"}'),s={name:"css/selectors/page_007.md"},g=Object.assign(s,{setup(a){return(l,t)=>(e(),o("div",null,t[0]||(t[0]=[r('<h1 id="каскадность-стилеи" tabindex="-1">Каскадность стилей <a class="header-anchor" href="#каскадность-стилеи" aria-label="Permalink to &quot;Каскадность стилей&quot;">​</a></h1><h2 id="что-такое-каскадность" tabindex="-1">Что такое каскадность? <a class="header-anchor" href="#что-такое-каскадность" aria-label="Permalink to &quot;Что такое каскадность?&quot;">​</a></h2><p><strong>Каскадность</strong> - это способ, которым браузер решает, какие стили применять, если для элемента задано несколько правил.</p><p>Каскад учитывает три основных фактора:</p><ul><li><strong>Источник стилей</strong> - браузерные стили, подключённые файлы CSS, внутренние стили (<em><strong><code>&lt;style&gt;</code></strong></em>), встроенные (<em><strong><code>style=&quot;&quot;</code></strong></em>);</li><li><strong>Специфичность селектора</strong> - чем более &quot;точный&#39; селектор, тем выше приоритет;</li><li><strong>Порядок объявления</strong> - при равной специфичности побеждает правило, записанное позже.</li></ul><h2 id="приоритет-каскада" tabindex="-1">Приоритет каскада <a class="header-anchor" href="#приоритет-каскада" aria-label="Permalink to &quot;Приоритет каскада&quot;">​</a></h2><ul><li><strong><code>!important</code></strong> - максимальная специфичность;</li><li><strong>Встроенные стили (<code>style=&quot;&quot;</code>)</strong> - приоритет в 1000 &quot;очков&quot;;</li><li><strong>ID-селекторы</strong> - приоритет в 100 &quot;очков&quot;;</li><li><strong>Классы, псевдоклассы, атрибуты</strong> - приоритет в 10 &quot;очков&quot;;</li><li><strong>Теги и псевдоэлементы</strong> - приоритет в 1 &quot;очко&quot;;</li><li><strong>Универсальный селектор (<code>*</code>)</strong> - приоритет в 0 &quot;очков&quot;;</li><li><strong>Порядок объявления</strong> - побеждает правило, записанное позже.</li></ul>',7)])))}});export{c as __pageData,g as default};
